    private void btnDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDividirActionPerformed
        // TODO add your handling code here:
        int nNum = Integer.parseInt(txtNum.getText());
        int nDen = Integer.parseInt(txtDen.getText());
        int nDiv = nNum / nDen ;
        int nResto = nNum % nDen ;
        lblDiv.setText(Integer.toString(nDiv));
        lblResto.setText(Integer.toString(nResto));
                                   
                
    }//GEN-LAST:event_btnDividirActionPerformed

