    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        int vInf = Integer.parseInt(valorInforme.getValue().toString());
        int vResto = vInf % 2 ;
        int aoCubo = (int)Math.pow(vInf,3);
        float raizQua = (float)Math.sqrt(vInf);
        float raizCub = (float)Math.cbrt(vInf);
        int vaAbs = Math.abs(vInf);
        
        
        lblResto.setText(Integer.toString(vResto));
        lblCubo.setText(Integer.toString(aoCubo));
        lblRaizQua.setText(Float.toString(raizQua));
        lblRaizCub.setText(Float.toString(raizCub));
        lblValorAbs.setText(Integer.toString(vaAbs));
        
        
        
        
        
    }//GEN-LAST:event_btnCalcularActionPerformed

